CREATE TABLE ssl_certificate (\n       id INT PRIMARY KEY CHECK (id = 1),\n       certificate TEXT,\n       private_key TEXT,\n       issued_at TIMESTAMP\n   );\n\n   CREATE TABLE api_certificate (\n       id INT PRIMARY KEY CHECK (id = 1),\n       certificate TEXT,\n       private_key TEXT,\n       issued_at TIMESTAMP\n   );\n   \n   CREATE TABLE client_certificate (\n       id INT PRIMARY KEY CHECK (id = 1),\n       certificate TEXT,\n       private_key TEXT,\n       issued_at TIMESTAMP\n   );