DECLARE @day INT = 12,\n        @month INT = 1,\n        @year INT = 2007\n\n-- Using CONVERT\nSELECT CONVERT(DATETIME, CAST(@year AS VARCHAR(4)) + '-' + \n                          RIGHT('0' + CAST(@month AS VARCHAR(2)), 2) + '-' + \n                          RIGHT('0' + CAST(@day AS VARCHAR(2)), 2))\n\n-- Using CAST\nSELECT CAST(CAST(@year AS VARCHAR(4)) + '-' + \n            RIGHT('0' + CAST(@month AS VARCHAR(2)), 2) + '-' + \n            RIGHT('0' + CAST(@day AS VARCHAR(2)), 2) AS DATETIME)