-- Customer table\nCREATE TABLE Customer (\n    customer_id INT AUTO_INCREMENT PRIMARY KEY,\n    customer_name VARCHAR(255),\n    customer_email VARCHAR(255),\n    -- Add other customer fields as needed\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Quote table\nCREATE TABLE Quote (\n    quote_id INT AUTO_INCREMENT PRIMARY KEY,\n    customer_id INT,\n    quote_date DATE,\n    total_amount DECIMAL(10, 2),\n    -- Add other quote fields as needed\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (customer_id) REFERENCES Customer(customer_id)\n);\n\n-- Quote_Products table\nCREATE TABLE Quote_Products (\n    quote_product_id INT AUTO_INCREMENT PRIMARY KEY,\n    quote_id INT,\n    product_name VARCHAR(255),\n    product_description TEXT,\n    quantity INT,\n    price_per_unit DECIMAL(10, 2),\n    total_price DECIMAL(10, 2) GENERATED ALWAYS AS (quantity * price_per_unit) STORED,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (quote_id) REFERENCES Quote(quote_id)\n);