CREATE OR REPLACE PROCEDURE GetPagedRecords (\n    p_page_number IN NUMBER,\n    p_page_size IN NUMBER,\n    p_cursor OUT SYS_REFCURSOR\n) AS\nBEGIN\n    OPEN p_cursor FOR\n    SELECT * FROM (\n        SELECT t.*, ROW_NUMBER() OVER (ORDER BY t.some_column) AS rn\n        FROM your_table t\n    )\n    WHERE rn BETWEEN (p_page_number - 1) * p_page_size + 1 AND p_page_number * p_page_size;\nEND;\n/