-- Creating schemas\nCREATE SCHEMA accounting;\nCREATE SCHEMA hr;\n\n-- Creating tables in different schemas\nCREATE TABLE accounting.Expenses (\n    ExpenseID INT PRIMARY KEY,\n    Amount DECIMAL(10, 2),\n    Description NVARCHAR(100)\n);\n\nCREATE TABLE hr.Employees (\n    EmployeeID INT PRIMARY KEY,\n    Name NVARCHAR(100),\n    Position NVARCHAR(50)\n);\n\n-- Assigning permissions to different roles\nGRANT SELECT ON accounting.Expenses TO AccountingRole;\nGRANT SELECT, INSERT, UPDATE ON hr.Employees TO HrRole;