DECLARE\n  subscription_list DBMS_AQ.AQ$_REG_INFO_LIST;\n  message_props    DBMS_AQ.MESSAGE_PROPERTIES_T;\n  message          VARCHAR2(100);\nBEGIN\n  -- Define the message\n  message := 'New data available';\n\n  -- Define the subscription list\n  subscription_list := DBMS_AQ.AQ$_REG_INFO_LIST(\n    DBMS_AQ.AQ$_REG_INFO('sub1', 'queue_name', 0),\n    DBMS_AQ.AQ$_REG_INFO('sub2', 'queue_name', 0)\n  );\n\n  -- Post the message\n  DBMS_AQ.POST(\n    subscription_list  => subscription_list,\n    message_properties => message_props,\n    payload            => message\n  );\n\n  COMMIT;\nEND;\n/