;WITH CTE AS (\n    SELECT \n        ContactID, \n        ContactAssociate,\n        ROW_NUMBER() OVER (PARTITION BY ContactAssociate ORDER BY NEWID()) AS RowNum,\n        COUNT(*) OVER (PARTITION BY ContactAssociate) AS TotalCount\n    FROM tb_Contact\n)\nUPDATE tb_Contact\nSET ContactAssociate = 'Peter'\nFROM tb_Contact\nJOIN CTE ON tb_Contact.ContactID = CTE.ContactID\nWHERE CTE.RowNum <= (CTE.TotalCount * 0.07);