CREATE OR REPLACE PROCEDURE GetFilteredData(\n    p_status_id IN table.status_id%TYPE,\n    p_date IN table.date%TYPE,\n    p_other_parameter IN table.other_parameter%TYPE\n)\nIS\n    v_query VARCHAR2(4000);\nBEGIN\n    v_query := 'SELECT * FROM table WHERE 1=1';\n    \n    IF p_status_id IS NOT NULL THEN\n        v_query := v_query || ' AND status_id = ' || p_status_id;\n    END IF;\n    \n    IF p_date IS NOT NULL THEN\n        v_query := v_query || ' AND date = TO_DATE(''' || p_date || ''', ''YYYY-MM-DD'')';\n    END IF;\n    \n    IF p_other_parameter IS NOT NULL THEN\n        v_query := v_query || ' AND other_parameter = ''' || p_other_parameter || '''';\n    END IF;\n    \n    EXECUTE IMMEDIATE v_query;\nEND;\n/