DELIMITER //\n\nCREATE PROCEDURE GetFilteredData(\n    IN p_status_id INT,\n    IN p_date DATE,\n    IN p_other_parameter VARCHAR(255)\n)\nBEGIN\n    SET @query = 'SELECT * FROM table WHERE 1=1';\n    \n    IF p_status_id IS NOT NULL THEN\n        SET @query = CONCAT(@query, ' AND status_id = ', p_status_id);\n    END IF;\n    \n    IF p_date IS NOT NULL THEN\n        SET @query = CONCAT(@query, ' AND date = "', p_date, '"');\n    END IF;\n    \n    IF p_other_parameter IS NOT NULL THEN\n        SET @query = CONCAT(@query, ' AND other_parameter = "', p_other_parameter, '"');\n    END IF;\n    \n    PREPARE stmt FROM @query;\n    EXECUTE stmt;\n    DEALLOCATE PREPARE stmt;\nEND //\n\nDELIMITER ;