IF @ID IS NULL OR @ID <= 0\nBEGIN\n    DECLARE @message VARCHAR(255)\n    IF @ID IS NULL\n    BEGIN\n        SET @message = 'ID is null. ID cannot be null or less than zero.'\n    END\n    ELSE\n    BEGIN\n        SET @message = 'ID is ' + CONVERT(VARCHAR, @ID) + '. ID cannot be null or less than zero.'\n    END\n    PRINT @message\n    RAISERROR 27001 'Custom error: ID validation failed.'\n    RETURN 27001\nEND