CREATE PROCEDURE GetFilteredData\n    @status_id INT = NULL,\n    @date DATE = NULL,\n    @other_parameter NVARCHAR(255) = NULL\nAS\nBEGIN\n    DECLARE @query NVARCHAR(MAX);\n    \n    SET @query = 'SELECT * FROM table WHERE 1=1';\n    \n    IF @status_id IS NOT NULL\n    BEGIN\n        SET @query = @query + ' AND status_id = ' + CAST(@status_id AS NVARCHAR);\n    END\n    \n    IF @date IS NOT NULL\n    BEGIN\n        SET @query = @query + ' AND date = ''' + CAST(@date AS NVARCHAR) + '''';\n    END\n    \n    IF @other_parameter IS NOT NULL\n    BEGIN\n        SET @query = @query + ' AND other_parameter = ''' + @other_parameter + '''';\n    END\n    \n    EXEC sp_executesql @query;\nEND