-- Define a TYPE for the table\nCREATE OR REPLACE TYPE my_table_type AS TABLE OF VARCHAR2(100);\n/\n\nDECLARE\n    -- Declare a variable of the above TYPE\n    my_table my_table_type;\nBEGIN\n    -- Initialize the collection\n    my_table := my_table_type();\n    \n    -- Add elements to the collection\n    my_table.EXTEND;\n    my_table(1) := 'First Element';\n    \n    my_table.EXTEND;\n    my_table(2) := 'Second Element';\n    \n    -- Access elements in the collection\n    FOR i IN 1..my_table.COUNT LOOP\n        DBMS_OUTPUT.PUT_LINE(my_table(i));\n    END LOOP;\nEND;\n/