-- Assuming the source data is in a table called SourceTable\n-- Step 1: Create the Parent Table\nCREATE TABLE ParentTable (\n    ID INT PRIMARY KEY AUTO_INCREMENT,\n    UserID INT,\n    `Group` VARCHAR(255),\n    Name VARCHAR(255)\n);\n\n-- Step 2: Insert distinct combinations of UserID and Group into ParentTable\nINSERT INTO ParentTable (UserID, `Group`, Name)\nSELECT DISTINCT UserId, `Group`, CONCAT('User ', UserId, ' - ', `Group`, ' data')\nFROM SourceTable;\n\n-- Step 3: Create the Child Table\nCREATE TABLE ChildTable (\n    ParentID INT,\n    Value VARCHAR(255),\n    FOREIGN KEY (ParentID) REFERENCES ParentTable(ID)\n);\n\n-- Step 4: Insert child records into ChildTable with the correct ParentID\nINSERT INTO ChildTable (ParentID, Value)\nSELECT p.ID, s.Value\nFROM SourceTable s\nJOIN ParentTable p\nON s.UserId = p.UserID AND s.`Group` = p.`Group`;