-- Create tables\nCREATE TABLE letters (\n    letter CHAR(1) PRIMARY KEY\n);\n\nCREATE TABLE numbers (\n    number INT PRIMARY KEY\n);\n\nCREATE TABLE mixed (\n    letter CHAR(1),\n    number INT,\n    PRIMARY KEY (letter, number)\n);\n\n-- Insert data\nINSERT INTO letters (letter) VALUES ('A'), ('B'), ('C');\nINSERT INTO numbers (number) VALUES (1), 2, 3;\nINSERT INTO mixed (letter, number) VALUES \n('A', 1), ('A', 2), ('B', 2), ('B', 3), ('C', 1), ('C', 3);\n\n-- Find missing entries\nSELECT l.letter, n.number\nFROM letters l, numbers n\nLEFT JOIN mixed m ON l.letter = m.letter AND n.number = m.number\nWHERE m.letter IS NULL;