SELECT /*+ INDEX(Exhibition venue_id_active_ends) */ \n    Exhibition.venue_id, Exhibition.name, Exhibition.slug, \n    Venue.name, Venue.slug, Venue.location_id, Venue.id, Exhibition.id \nFROM exhibitions AS Exhibition \nLEFT JOIN venues AS Venue ON (Exhibition.venue_id = Venue.id) \nLEFT JOIN temperatures AS Temperature ON (Temperature.ref = Exhibition.id) \nWHERE Exhibition.active = '1' \n  AND Exhibition.ends <= CURDATE() \nORDER BY Temperature.temperature DESC \nLIMIT 5;