CREATE OR REPLACE PROCEDURE yearly_summary_proc IS\n    last_id NUMBER;\nBEGIN\n    -- Retrieve the last processed ID\n    SELECT last_id_processed INTO last_id FROM job_control_table FOR UPDATE;\n\n    -- Perform the update for the next 100 rows\n    UPDATE your_table\n    SET some_column = some_calculation\n    WHERE primary_key > last_id\n    AND ROWNUM <= 100\n    RETURNING primary_key INTO last_id;\n\n    -- Update the control table with the new last processed ID\n    UPDATE job_control_table SET last_id_processed = last_id;\n\n    COMMIT;\nEND;\n/