-- Example of Normalized Schema\nCREATE TABLE Customers (\n    CustomerID INT PRIMARY KEY,\n    CustomerName VARCHAR(100)\n);\n\nCREATE TABLE Orders (\n    OrderID INT PRIMARY KEY,\n    CustomerID INT,\n    OrderDate DATE,\n    FOREIGN KEY (CustomerID) REFERENCES Customers(CustomerID)\n);\n\n-- Example of Denormalized Schema\nCREATE TABLE CustomerOrders (\n    OrderID INT PRIMARY KEY,\n    CustomerID INT,\n    CustomerName VARCHAR(100),\n    OrderDate DATE\n);\n\n-- Insert and Query Performance Test\n-- Normalized Insert\nINSERT INTO Customers (CustomerID, CustomerName) VALUES (1, 'John Doe');\nINSERT INTO Orders (OrderID, CustomerID, OrderDate) VALUES (1, 1, '2023-10-01');\n\n-- Denormalized Insert\nINSERT INTO CustomerOrders (OrderID, CustomerID, CustomerName, OrderDate) VALUES (1, 1, 'John Doe', '2023-10-01');\n\n-- Normalized Query\nSELECT Customers.CustomerName, Orders.OrderDate\nFROM Customers\nJOIN Orders ON Customers.CustomerID = Orders.CustomerID\nWHERE Orders.OrderID = 1;\n\n-- Denormalized Query\nSELECT CustomerName, OrderDate\nFROM CustomerOrders\nWHERE OrderID = 1;