DECLARE\n    v_sql    CLOB;\n    v_value  VARCHAR2(100) := '1/22/2008P09RR8';\nBEGIN\n    FOR t IN (SELECT table_name, column_name \n              FROM all_tab_columns \n              WHERE data_type IN ('VARCHAR2', 'CHAR', 'NVARCHAR2', 'NCHAR'))\n    LOOP\n        v_sql := v_sql || 'SELECT ''' || t.table_name || ''' AS table_name, ''' || t.column_name || ''' AS column_name, COUNT(*) AS matches FROM ' || t.table_name || ' WHERE ' || t.column_name || ' = ''' || v_value || ''' UNION ALL ';\n    END LOOP;\n\n    -- Remove the trailing ' UNION ALL '\n    v_sql := RTRIM(v_sql, ' UNION ALL ');\n\n    EXECUTE IMMEDIATE 'CREATE OR REPLACE VIEW search_results AS ' || v_sql;\nEND;\n/\n\nSELECT * FROM search_results WHERE matches > 0;