-- Example of creating a backup\nBACKUP DATABASE YourDatabase TO DISK = 'C:\Backups\YourDatabase_YYYYMMDD.bak'\n\n-- Example of a log backup\nBACKUP LOG YourDatabase TO DISK = 'C:\Backups\YourDatabase_Log_YYYYMMDD.trn'\n\n-- Example of checking backup history\nSELECT \n    backup_finish_date,\n    database_name,\n    backup_type,\n    physical_device_name\nFROM \n    msdb.dbo.backupset bs\nJOIN \n    msdb.dbo.backupmediafamily bmf ON bs.media_set_id = bmf.media_set_id\nORDER BY \n    backup_finish_date DESC