ALTER TRIGGER [MyTableInsertDeleteUpdate]\n  ON  [dbo].[MyTable]\n  AFTER  INSERT, DELETE, UPDATE\nAS\nBEGIN\n    -- Insert records into the audit table for each inserted/updated row\n    INSERT INTO AuditTable (id, action)\n    SELECT id, 'inserted/updated'\n    FROM inserted\n    WHERE id IS NOT NULL;\n\n    -- Print the ids for debugging purposes (optional)\n    SELECT 'inserted/updated id: ' + CAST(id AS VARCHAR) AS debug_message\n    FROM inserted\n    WHERE id IS NOT NULL;\nEND;