ALTER TRIGGER [MyTableInsertDeleteUpdate]\n  ON  [dbo].[MyTable]\n  AFTER  INSERT, DELETE, UPDATE\nAS\nBEGIN\n    -- Declare a cursor to iterate over each row in the inserted table\n    DECLARE @id INT;\n    DECLARE cursor_inserted CURSOR FOR\n    SELECT id FROM inserted;\n\n    OPEN cursor_inserted;\n\n    FETCH NEXT FROM cursor_inserted INTO @id;\n\n    WHILE @@FETCH_STATUS = 0\n    BEGIN\n        IF (@id IS NOT NULL)\n        BEGIN\n            -- Insert a new record to the audit table\n            PRINT 'inserted/updated id: ' + CAST(@id AS VARCHAR);\n        END\n\n        FETCH NEXT FROM cursor_inserted INTO @id;\n    END;\n\n    CLOSE cursor_inserted;\n    DEALLOCATE cursor_inserted;\nEND;