SELECT \n    q.question_id,\n    q.question_text,\n    a.answer_id,\n    a.answer_text,\n    COUNT(aq.user_ip) AS answer_count\nFROM \n    Questions q\nINNER JOIN \n    Answers a ON q.question_id = a.question_id\nLEFT JOIN \n    Answered_Questions aq ON a.question_id = aq.question_id AND a.answer_id = aq.answer_id\nWHERE \n    q.created_at = (SELECT MAX(created_at) FROM Questions)\nGROUP BY \n    q.question_id, q.question_text, a.answer_id, a.answer_text\nORDER BY \n    q.created_at DESC;