-- Insert intermediate results into temporary table\n   SELECT MainTable.PK, OtherFields, AggregateFunctions INTO TempTable\n   FROM MainTable\n   LEFT JOIN SubQuery ON MainTable.PK = SubQuery.PK\n   WHERE Conditions\n   HAVING ConditionsOnPK;\n\n   -- Fetch results including the memo field\n   SELECT TempTable.*, MainTable.MemoField\n   FROM TempTable\n   LEFT JOIN MainTable ON TempTable.PK = MainTable.PK;