-- Create a sample table\n    CREATE TABLE TestData (\n        ID INT IDENTITY(1,1) PRIMARY KEY,\n        Name NVARCHAR(50),\n        Age INT,\n        CreatedDate DATETIME\n    );\n\n    -- Insert random data\n    DECLARE @i INT = 0;\n    WHILE @i < 1000000\n    BEGIN\n        INSERT INTO TestData (Name, Age, CreatedDate)\n        VALUES (\n            LEFT(NEWID(), 10), -- Random string for Name\n            ABS(CHECKSUM(NEWID()) % 100), -- Random Age between 0 and 99\n            DATEADD(DAY, ABS(CHECKSUM(NEWID()) % 3650), '2000-01-01') -- Random date within 10 years\n        );\n        SET @i = @i + 1;\n    END;