CREATE OR REPLACE TYPE t_id_list IS TABLE OF NUMBER;\n\nCREATE OR REPLACE FUNCTION EXECUTE_UPDATE (\n  id_list IN t_id_list,\n  value   IN NUMBER\n) RETURN NUMBER IS\nBEGIN\n  UPDATE table1\n  SET col1 = col1 - value\n  WHERE id IN (SELECT COLUMN_VALUE FROM TABLE(id_list));\n\n  RETURN SQL%ROWCOUNT;\nEND;\n/