-- Course table to store course information\nCREATE TABLE Course (\n    CourseID INT PRIMARY KEY AUTO_INCREMENT,\n    DeptPrefix VARCHAR(10),\n    Number INT,\n    Title VARCHAR(255),\n    Description TEXT,\n    StartTime TIME,\n    EndTime TIME\n);\n\n-- DaysOfWeek table to store the days of the week\nCREATE TABLE DaysOfWeek (\n    DayID INT PRIMARY KEY AUTO_INCREMENT,\n    DayName VARCHAR(10) -- e.g., 'Monday', 'Tuesday', etc.\n);\n\n-- CourseDays table to create a many-to-many relationship between Course and DaysOfWeek\nCREATE TABLE CourseDays (\n    CourseID INT,\n    DayID INT,\n    PRIMARY KEY (CourseID, DayID),\n    FOREIGN KEY (CourseID) REFERENCES Course(CourseID),\n    FOREIGN KEY (DayID) REFERENCES DaysOfWeek(DayID)\n);