CREATE TABLE User (\n    id INT PRIMARY KEY,\n    username VARCHAR(50),\n    created_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    updated_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP\n);\n\nCREATE TABLE Customer (\n    id INT PRIMARY KEY,\n    name VARCHAR(100),\n    created_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    updated_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP\n);\n\nCREATE TABLE Post (\n    id INT PRIMARY KEY,\n    content TEXT,\n    created_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    updated_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP\n);