-- Identify sessions that are being blocked\n   SELECT \n       session_id AS blocking_session,\n       COUNT(*)  AS count\n   FROM \n       dba_blockers\n   GROUP BY \n       session_id;\n\n   -- Identify waiting sessions and the sessions they are waiting for\n   SELECT \n       w.session_id AS waiting_session,\n       w.lock_type AS waiting_lock_type,\n       w.mode_held AS waiting_mode_held,\n       w.mode_requested AS waiting_mode_requested,\n       b.session_id AS blocking_session\n   FROM \n       dba_waiters w\n   JOIN \n       dba_blockers b ON w.blocking_session = b.session_id;