CREATE TABLE EntityLog (\n    id INT PRIMARY KEY AUTO_INCREMENT,\n    entity_name VARCHAR(50),\n    entity_id INT,\n    action VARCHAR(10), -- 'CREATE' or 'UPDATE'\n    timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);\n\nCREATE TABLE User (\n    id INT PRIMARY KEY,\n    username VARCHAR(50)\n);\n\nCREATE TABLE Customer (\n    id INT PRIMARY KEY,\n    name VARCHAR(100)\n);\n\nCREATE TABLE Post (\n    id INT PRIMARY KEY,\n    content TEXT\n);