-- Example of an auditing trigger\nCREATE TRIGGER AuditTrigger\nON YourTable\nAFTER UPDATE\nAS\nBEGIN\n    IF (TRIGGER_NESTLEVEL() > 1) RETURN;\n\n    -- Your auditing logic here\n    INSERT INTO AuditTable (UpdatedTime, UpdatedBy)\n    SELECT GETDATE(), SUSER_SNAME()\n    FROM Inserted;\nEND;\n\n-- Example of an events trigger\nCREATE TRIGGER EventsTrigger\nON YourTable\nAFTER UPDATE\nAS\nBEGIN\n    IF (TRIGGER_NESTLEVEL() > 1) RETURN;\n\n    -- Your event logging logic here\n    INSERT INTO EventsTable (EventTime, EventDescription)\n    SELECT GETDATE(), 'Record updated'\n    FROM Inserted;\nEND;