-- Using a CTE to avoid temporary table\nWITH CTE_MatchResult AS (\n    SELECT MatchId \n    FROM MatchResult \n    WHERE SOME_CONDITION\n)\n-- Deleting from MatchEvent\nDELETE ME\nFROM MatchEvent ME\nINNER JOIN CTE_MatchResult MR ON ME.MatchId = MR.MatchId;\n\n-- Deleting from MatchResult\nDELETE MR\nFROM MatchResult MR\nINNER JOIN CTE_MatchResult CMR ON MR.MatchId = CMR.MatchId;