CREATE TABLE Users (\n    userID UNIQUEIDENTIFIER DEFAULT NEWID() PRIMARY KEY,\n    username NVARCHAR(256) NOT NULL UNIQUE, -- preferably email\n    passwordHash NVARCHAR(256) NOT NULL,\n    passwordSalt NVARCHAR(256) NOT NULL,\n    onceUsePassword BIT NOT NULL DEFAULT 0,\n    alternativeContactEmail NVARCHAR(256),\n    userStatusID INT NOT NULL, -- FK to a lookup table with statuses like: active, disabled, etc.\n    dateCreated DATETIME NOT NULL DEFAULT GETDATE(),\n    dateUpdated DATETIME NOT NULL,\n    lastPasswordUpdate DATETIME NOT NULL,\n    lastLogon DATETIME,\n    failedLoginAttempts INT DEFAULT 0,\n    accountLockout BIT NOT NULL DEFAULT 0,\n    forename NVARCHAR(256),\n    surname NVARCHAR(256)\n);