-- Replace 'specific_post_id' with the ID of the specific post you are interested in.\nSET @specific_post_id = 1;\n\n-- Step 1: Find posts with all the tags of the specific post\nSELECT p.*\nFROM Posts p\nJOIN Post_tag_nn pt ON p.post_id = pt.post_id\nWHERE pt.tag_id IN (\n    SELECT tag_id\n    FROM Post_tag_nn\n    WHERE post_id = @specific_post_id\n)\nGROUP BY p.post_id\nHAVING COUNT(DISTINCT pt.tag_id) = (\n    SELECT COUNT(DISTINCT tag_id)\n    FROM Post_tag_nn\n    WHERE post_id = @specific_post_id\n);\n\n-- Step 2: Find posts with any three of the tags of the specific post\nSELECT p.*\nFROM Posts p\nJOIN Post_tag_nn pt ON p.post_id = pt.post_id\nWHERE pt.tag_id IN (\n    SELECT tag_id\n    FROM Post_tag_nn\n    WHERE post_id = @specific_post_id\n)\nGROUP BY p.post_id\nHAVING COUNT(DISTINCT pt.tag_id) = 3;\n\n-- Step 3: Find posts with any two of the tags of the specific post\nSELECT p.*\nFROM Posts p\nJOIN Post_tag_nn pt ON p.post_id = pt.post_id\nWHERE pt.tag_id IN (\n    SELECT tag_id\n    FROM Post_tag_nn\n    WHERE post_id = @specific_post_id\n)\nGROUP BY p.post_id\nHAVING COUNT(DISTINCT pt.tag_id) = 2;\n\n-- Step 4: Find posts with any one of the tags of the specific post\nSELECT p.*\nFROM Posts p\nJOIN Post_tag_nn pt ON p.post_id = pt.post_id\nWHERE pt.tag_id IN (\n    SELECT tag_id\n    FROM Post_tag_nn\n    WHERE post_id = @specific_post_id\n)\nGROUP BY p.post_id\nHAVING COUNT(DISTINCT pt.tag_id) = 1;