-- Contacts table\nCREATE TABLE Contacts (\n    Id INT PRIMARY KEY,\n    FirstName NVARCHAR(50),\n    LastName NVARCHAR(50),\n    Email NVARCHAR(100)\n    -- Other contact-specific fields\n);\n\n-- PhoneNumbers table\nCREATE TABLE PhoneNumbers (\n    Id INT PRIMARY KEY,\n    ContactId INT FOREIGN KEY REFERENCES Contacts(Id),\n    PhoneNumber NVARCHAR(22),\n    Description NVARCHAR(100),\n    Comment NVARCHAR(255) -- Optional additional comment field\n);