-- Example table and data\nCREATE TABLE ExampleTable (\n    Id INT PRIMARY KEY,\n    LocalDateTime DATETIME\n);\n\nINSERT INTO ExampleTable (Id, LocalDateTime) VALUES\n(1, '2023-03-10 10:00:00'), -- Standard time\n(2, '2023-07-10 10:00:00'); -- Daylight saving time\n\n-- Update script to convert local time to UTC\nUPDATE ExampleTable\nSET LocalDateTime = \n    CAST(LocalDateTime AT TIME ZONE 'Eastern Standard Time' AT TIME ZONE 'UTC' AS DATETIME);