-- Create a table to hold the words\nCREATE TEMPORARY TABLE words (\n    word VARCHAR(200)\n);\n\n-- Insert words into the words table\nINSERT INTO words (word)\nSELECT LOWER(SUBSTRING_INDEX(SUBSTRING_INDEX(t.text, ' ', n.n), ' ', -1)) word\nFROM your_table t \nCROSS JOIN (\n    SELECT a.N + b.N * 10 + 1 n\n    FROM (SELECT 0 N UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 \n          UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) a\n    ,(SELECT 0 N UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 \n          UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) b\n    ORDER BY n\n) n\nWHERE n.n <= 1 + (LENGTH(t.text) - LENGTH(REPLACE(t.text, ' ', '')));\n\n-- Create a table to hold the words to ignore\nCREATE TEMPORARY TABLE ignore_words (\n    word VARCHAR(200)\n);\n\n-- Insert words to ignore into the ignore_words table\nINSERT INTO ignore_words (word) VALUES ('i'), ('have'), ('will');\n\n-- Select the most common words excluding those in the ignore list\nSELECT word, COUNT(*) as occurrences\nFROM words\nWHERE word NOT IN (SELECT word FROM ignore_words)\nGROUP BY word\nORDER BY occurrences DESC;