WITH submitter_summary AS (\n    SELECT \n        submitter_id,\n        COUNT(DISTINCT CASE WHEN MONTH(submission_date) BETWEEN 1 AND 6 THEN 1 END) AS jan_jun_count,\n        COUNT(DISTINCT CASE WHEN MONTH(submission_date) BETWEEN 7 AND 12 THEN 1 END) AS jul_dec_count\n    FROM \n        submissions\n    GROUP BY \n        submitter_id\n)\nSELECT \n    submitter_id\nFROM \n    submitter_summary\nWHERE \n    jan_jun_count > 0 AND jul_dec_count = 0;