SELECT ID, PRODUCT_NAME, COLOR, SALE_COUNT\nFROM (\n    SELECT *,\n           ROW_NUMBER() OVER (PARTITION BY SUBSTRING(PRODUCT_NAME FROM '^[^ ]+') \n                              ORDER BY SALE_COUNT DESC) as rn\n    FROM products\n    WHERE PRODUCT_NAME ILIKE '%mp3 player%'\n) subquery\nWHERE rn = 1\n   OR PRODUCT_NAME ILIKE '%mp3 player red%'\nORDER BY PRODUCT_NAME ILIKE '%mp3 player red%' DESC;