SELECT Source.ItemName, \nNZ(Count(IIF(Int(Date()-Source.DateAdded) > 20, 1, NULL)), 0) AS Total, \nNZ(Count(IIF(Int(Date()-Source.DateAdded) BETWEEN 20 AND 44, 1, NULL)), 0) AS BTWN_20_44, \nNZ(Count(IIF(Int(Date()-Source.DateAdded) BETWEEN 45 AND 60, 1, NULL)), 0) AS BTWN_45_60, \nNZ(Count(IIF(Int(Date()-Source.DateAdded) BETWEEN 61 AND 90, 1, NULL)), 0) AS BTWN_61_90, \nNZ(Count(IIF(Int(Date()-Source.DateAdded) BETWEEN 91 AND 180, 1, NULL)), 0) AS BTWN_91_180, \nNZ(Count(IIF(Int(Date()-Source.DateAdded) > 180, 1, NULL)), 0) AS GT_180\nFROM Source\nGROUP BY Source.ItemName;