-- Finding records that are in AccessTable but not in SMSTable\nSELECT a.*\nFROM AccessTable a\nLEFT JOIN SMSTable s\nON a.Name = s.displayName OR a.NetworkID = s.SAMAccountName OR a.ComputerName = s.NetbiosName\nWHERE s.NetbiosName IS NULL AND s.SAMAccountName IS NULL AND s.displayName IS NULL\n\nUNION ALL\n\n-- Finding records that are in SMSTable but not in AccessTable\nSELECT s.*\nFROM SMSTable s\nLEFT JOIN AccessTable a\nON s.displayName = a.Name OR s.SAMAccountName = a.NetworkID OR s.NetbiosName = a.ComputerName\nWHERE a.Name IS NULL AND a.NetworkID IS NULL AND a.ComputerName IS NULL;