-- Step 1: Retrieve and store the current DATEFORMAT setting\nDECLARE @currentDateFormat NVARCHAR(20);\nSELECT @currentDateFormat = value\nFROM (\n    DBCC USEROPTIONS WITH NO_INFOMSGS\n) AS options\nWHERE options.[Set Option] = 'dateformat';\n\n-- Step 2: Set the DATEFORMAT to your preferred format (e.g., MDY for month/day/year)\nSET DATEFORMAT MDY;\n\n-- Step 3: Execute your INSERT statements\n-- INSERT INTO your_table (your_columns) VALUES (your_values);\n\n-- Step 4: Reset the DATEFORMAT to the stored value\nIF @currentDateFormat IS NOT NULL\nBEGIN\n    EXEC('SET DATEFORMAT ' + @currentDateFormat);\nEND