BEGIN\n       DBMS_RESOURCE_MANAGER.CREATE_PLAN(\n          plan    => 'disconnect_idle_sessions',\n          comment => 'Plan to disconnect idle sessions');\n    \n       DBMS_RESOURCE_MANAGER.CREATE_PLAN_DIRECTIVE(\n          plan             => 'disconnect_idle_sessions',\n          group_or_subplan => 'OTHER_GROUPS',\n          comment          => 'Other groups',\n          switch_group     => '',\n          switch_time      => 3600, -- Time in seconds after which the session is switched\n          switch_estimate  => TRUE,\n          max_idle_blocker_time => 600); -- Max idle time in seconds before disconnect\n     \n       DBMS_RESOURCE_MANAGER.VALIDATE_PENDING_AREA;\n       DBMS_RESOURCE_MANAGER.SUBMIT_PENDING_AREA;\n    END;\n    /