-- Create a master key\n    CREATE MASTER KEY ENCRYPTION BY PASSWORD = 'YourStrongPassword';\n    \n    -- Create a certificate\n    CREATE CERTIFICATE MyCertificate WITH SUBJECT = 'My Certificate';\n    \n    -- Create a symmetric key\n    CREATE SYMMETRIC KEY MySymmetricKey\n    WITH ALGORITHM = AES_256\n    ENCRYPTION BY CERTIFICATE MyCertificate;\n    \n    -- Open the symmetric key\n    OPEN SYMMETRIC KEY MySymmetricKey\n    DECRYPTION BY CERTIFICATE MyCertificate;\n    \n    -- Encrypt data\n    DECLARE @EncryptedData VARBINARY(MAX);\n    SET @EncryptedData = EncryptByKey(Key_GUID('MySymmetricKey'), 'SensitiveData');\n    \n    -- Decrypt data\n    DECLARE @DecryptedData NVARCHAR(MAX);\n    SET @DecryptedData = CONVERT(NVARCHAR(MAX), DecryptByKey(@EncryptedData));\n    \n    -- Close the symmetric key\n    CLOSE SYMMETRIC KEY MySymmetricKey;