CREATE OR REPLACE PROCEDURE fetch_data_by_id (p_id IN VARCHAR2)\nIS\n    TYPE t_info_data IS RECORD (\n        info_field  VARCHAR2(100),\n        data_field  VARCHAR2(100)\n    );\n\n    TYPE t_info_data_table IS TABLE OF t_info_data;\n\n    l_info_data t_info_data_table;\n    l_sql       VARCHAR2(4000);\n    l_cur       SYS_REFCURSOR;\nBEGIN\n    FOR table_name IN (\n        SELECT 'table_one' AS table_name FROM dual UNION ALL\n        SELECT 'table_two' FROM dual UNION ALL\n        SELECT 'table_three' FROM dual -- Add more tables as needed\n    )\n    LOOP\n        l_sql := 'SELECT info_field, data_field FROM ' || table_name.table_name || ' WHERE some_id = :id';\n        \n        OPEN l_cur FOR l_sql USING p_id;\n        FETCH l_cur BULK COLLECT INTO l_info_data;\n        CLOSE l_cur;\n\n        -- Process the fetched data as needed, for example, you can print it\n        FOR i IN 1..l_info_data.COUNT LOOP\n            dbms_output.put_line('Info Field: ' || l_info_data(i).info_field || ', Data Field: ' || l_info_data(i).data_field);\n        END LOOP;\n    END LOOP;\nEND fetch_data_by_id;\n/