CREATE EVENT SESSION [TrackRecompiles] ON SERVER\n   ADD EVENT sqlserver.sp_statement_completed(\n       ACTION(sqlserver.sql_text, sqlserver.database_id, sqlserver.client_hostname, sqlserver.username)\n       WHERE ([package0].[equal_uint64]([object_type], (8272)) AND [sqlserver].[is_recompile] = 1))\n   ADD TARGET package0.event_file(SET filename=N'C:\Temp\TrackRecompiles.xel');\n   \n   ALTER EVENT SESSION [TrackRecompiles] ON SERVER\n   STATE = START;