IF EXISTS (SELECT 1 FROM sys.columns WHERE Name = N'MyColumn' AND Object_ID = Object_ID(N'MyTable'))\nBEGIN\n    -- Use dynamic SQL to alter the column and update it\n    DECLARE @sql NVARCHAR(MAX)\n\n    -- Construct the ALTER TABLE statement\n    SET @sql = N'ALTER TABLE MyTable ALTER COLUMN MyColumn VARCHAR(25);'\n\n    -- Execute the ALTER TABLE statement\n    EXEC sp_executesql @sql\n\n    -- Construct the UPDATE statement\n    SET @sql = N'UPDATE MyTable SET MyColumn = SUBSTRING(MyColumn, 0, 5);'\n\n    -- Execute the UPDATE statement\n    EXEC sp_executesql @sql\nEND