CREATE TABLE buildings (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    location VARCHAR(255) NOT NULL,\n    name VARCHAR(255) NOT NULL,\n    type ENUM('House', 'Office') NOT NULL\n);\n\nCREATE TABLE houses (\n    id INT PRIMARY KEY,\n    numRooms INT,\n    numBathrooms INT,\n    FOREIGN KEY (id) REFERENCES buildings(id)\n);\n\nCREATE TABLE offices (\n    id INT PRIMARY KEY,\n    offices INT,\n    sqfoot INT,\n    FOREIGN KEY (id) REFERENCES buildings(id)\n);