SELECT \n    u.id AS user_id, \n    u.name, \n    u.field2, \n    u.field3, \n    n.id AS note_id, \n    n.subject, \n    n.heading, \n    n.body, \n    n.timestamp\nFROM \n    users u\nLEFT JOIN \n    notes n ON u.id = n.user_id\nINNER JOIN (\n    SELECT \n        user_id, \n        MAX(timestamp) AS latest_timestamp\n    FROM \n        notes\n    GROUP BY \n        user_id\n) latest_notes ON n.user_id = latest_notes.user_id AND n.timestamp = latest_notes.latest_timestamp;