-- Union of three queries\nWITH UnionResult AS (\n    SELECT column1, column2, column3 FROM Table1\n    UNION ALL\n    SELECT column1, column2, column3 FROM Table2\n    UNION ALL\n    SELECT column1, column2, column3 FROM Table3\n)\n\n-- Pivot query\nSELECT \n    PivotColumn,\n    MAX(CASE WHEN ColumnName = 'Value1' THEN Value END) AS Column1,\n    MAX(CASE WHEN ColumnName = 'Value2' THEN Value END) AS Column2,\n    MAX(CASE WHEN ColumnName = 'Value3' THEN Value END) AS Column3\nFROM \n    UnionResult\nGROUP BY \n    PivotColumn;