SELECT \n    p.Partid, \n    p.Partnumber, \n    p.Description, \n    pr.Price, \n    pr.PriceDate\nFROM \n    MyParts p\nLEFT JOIN \n    MyPrices pr ON p.Partid = pr.Partid\nINNER JOIN \n    (\n        SELECT \n            Partid, \n            MAX(PriceDate) AS MaxPriceDate\n        FROM \n            MyPrices\n        GROUP BY \n            Partid\n    ) sub ON pr.Partid = sub.Partid AND pr.PriceDate = sub.MaxPriceDate;